import React, { useState, useEffect } from "react";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom";
import Pagination from "../../components/Pagination/Pagination";

const Houses = () => {
    const [houses, setHouses] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalHouses, setTotalHouses] = useState(0);
    const [error, setError] = useState(null);

    const totalPages = Math.ceil(totalHouses / pageSize);

    useEffect(() => {
        const fetchHouses = async () => {
            try {
                const response = await IceAndFireApi.getHousesWithPagination(currentPage, pageSize);
                console.log("API Response:", response);

                if (response.data.length === 0) {
                    console.log("No more houses to list.");
                } else {
                    setHouses(response.data);
                    setTotalHouses(response.total);
                }
            } catch (err) {
                setError("Error fetching houses. Please try again later.");
            }
        };

        fetchHouses();
        console.log("currentPage:", currentPage);
        console.log("totalPages:", totalPages);
    }, [currentPage, pageSize, setTotalHouses]);

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    const handlePageSizeChange = (newSize) => {
        setPageSize(newSize);
        setCurrentPage(1);
    };

    console.log("Render - currentPage:", currentPage);
    console.log("Render - totalPages:", totalPages);

    return (
        <div>
            <h1>Houses of Westeros and Beyond</h1>
            { error && <p>{ error }</p> }
            <ul>
                { houses.map((house) => (
                    <li key={ house.url }>
                        <Link to={ `/houses/${house.url.split("/").pop()}` }>
                            <strong>{ house.name }</strong>
                            <br />
                            Titles: { house.titles.join(", ") || "No Titles" }
                            <br />
                            Current Lord:{ " " }
                            { house.currentLord ? (
                                <Link to={ `/characters/${house.currentLord.split("/").pop()}` }>{ house.currentLord }</Link>
                            ) : (
                                "N/A"
                            ) }
                            <br />
                            Sworn Members:{ " " }
                            { house.swornMembers &&
                                house.swornMembers.map((memberUrl) => (
                                    <Link key={ memberUrl } to={ `/characters/${memberUrl.split("/").pop()}` }>
                                        {house}
                                    </Link>
                                )) }
                        </Link>
                    </li>
                )) }
            </ul>
            <Pagination currentPage={ currentPage } totalPages={ totalPages } onPageChange={ handlePageChange } />
            <select value={ pageSize } onChange={ (e) => handlePageSizeChange(parseInt(e.target.value, 10)) }>
                <option value={ 10 }>10 per page</option>
                <option value={ 20 }>20 per page</option>
                <option value={ 50 }>50 per page</option>
            </select>
        </div>
    );
};

export default Houses;
