import React, { useState, useEffect } from "react";
import Pagination from "../../components/Pagination/Pagination";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom"

/**
 * Characters component to display a list of characters.
 * @returns {JSX.Element} The Characters component JSX.
 */
const Characters = () => {
    //Initialize state variables for characters, current page, page size, and total characters
    const [characters, setCharacters] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalCharacters, setTotalCharacters] = useState(0);
    useEffect(() => {
        /**
         * Fetches characters from the API and updates the component state.
         */
        const fetchCharacters = async () => {
            try {
                const response = await IceAndFireApi.getCharacters();
                setCharacters(response);
                setTotalCharacters(response.length);
            } catch (error) {
                console.error("Error fetching characters:", error);
            }
        };
        //Fetch characters when the component mounts
        fetchCharacters();
    }, []);
    return (
        <div>
            <h1>Characters of Westeros and Beyond</h1>
            <ul>
                { characters.map((character) => (
                    <li key={ character.url }>
                        <Link to={ `/characters/${character.url.split("/").pop()}` }>
                            { character.name || (character.aliases.length > 0 ? character.aliases[0] : "Unnamed") }
                            <br />
                            Culture: { character.culture || "Unknown" }
                            <br />
                            Gender: { character.gender === "male" ? "♂️" : character.gender === "female" ? "♀️" : "Unknown" }
                        </Link>
                    </li>
                )) }
            </ul>
            <Pagination
                currentPage={ currentPage }
                pageSize={ pageSize }
                totalItems={ totalCharacters }
                onPageChange={ (page) => setCurrentPage(page) }
                onPageSizeChange={ (size) => setPageSize(size) }
            />
        </div>
    );
};

export default Characters;
