// IceAndFireApi.js
import axios from "axios";

const BASE_URL = "https://anapioficeandfire.com/api";

class IceAndFireApi {
    static async fetchData(url) {
        try {
            const response = await axios.get(url);
            return response.data;
        } catch (error) {
            throw error;
        }
    }

    static async getCharactersWithPagination(page, pageSize) {
        const url = `${BASE_URL}/characters?page=${page}&pageSize=${pageSize}`;
        return this.fetchData(url);
    }

    static async getCharacterById(id) {
        const url = `${BASE_URL}/characters/${id}`;
        return this.fetchData(url);
    }

    static async getHousesWithPagination(page, pageSize) {
        const url = `${BASE_URL}/houses?page=${page}&pageSize=${pageSize}`;
        return this.fetchData(url);
    }

    static async getHouses() {
        const url = `${BASE_URL}/houses`;
        return this.fetchData(url);
    }

    static async getAllegianceByUrl(url) {
        return this.fetchData(url);
    }
}

export default IceAndFireApi;
