import React, { useState, useEffect } from "react";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom";
import { Container, Row, Col, Card, Pagination, Form } from "react-bootstrap"

const Characters = () => {
    const [characters, setCharacters] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalCharacters, setTotalCharacters] = useState(0);
    const [error, setError] = useState(null);

    const totalPages = Math.ceil(totalCharacters / pageSize);

    useEffect(() => {
        const fetchCharacters = async () => {
            try {
                const response = await IceAndFireApi.getCharactersWithPagination(currentPage, pageSize);
                console.log("API Response:", response);
                console.log("currentPage:", currentPage);
                console.log("totalPages:", Math.ceil(response.total / pageSize));
                console.log("Character URLs:", response.data.map(character => character.url));
                setCharacters(response.data);
                setTotalCharacters(response.total);
            } catch (error) {
                setError("Error fetching characters. Please try again later.");
            }
        };

        fetchCharacters();
    }, [currentPage, pageSize]);

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    const handlePageSizeChange = (newSize) => {
        setPageSize(newSize);
        setCurrentPage(1);
    };

    return (
        <Container>
            <h1 className="mt-4 mb-4">Characters of Westeros and Beyond</h1>
            { error && <p>{ error }</p> }
            <Row>
                { characters.map((character) => (
                    <Col key={ character.url } sm={ 6 } md={ 4 } lg={ 3 } xl={ 2 } className="mb-4">
                        <Card>
                            <Card.Body>
                                <Card.Title>
                                    <Link to={ `/characters/${character.url.split("/").pop()}` }>{ character.name || "Unnamed" }</Link>
                                </Card.Title>
                                <Card.Text>
                                    <strong>Culture:</strong> { character.culture || "Unknown" }
                                    <br />
                                    <strong>Gender:</strong> { character.gender === "Male" ? "♂️" : character.gender === "Female" ? "♀️" : "Unknown" }
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </Col>
                )) }
            </Row>
            <Row className="mt-4">
                <Col>
                    <Pagination>
                        <Pagination.Prev onClick={ () => handlePageChange(currentPage - 1) } disabled={ currentPage === 1 } />
                        <Pagination.Next onClick={ () => handlePageChange(currentPage + 1) } disabled={ currentPage === totalPages } />
                    </Pagination>
                </Col>
                <Col xs={ 6 } md={ 4 } className="text-right">
                    <Form.Control as="select" value={ pageSize } onChange={ (e) => handlePageSizeChange(parseInt(e.target.value, 10)) }>
                        <option value={ 10 }>10 per page</option>
                        <option value={ 20 }>20 per page</option>
                        <option value={ 50 }>50 per page</option>
                    </Form.Control>
                </Col>
            </Row>
        </Container>
    );
};

export default Characters;
