import React, { useState, useEffect } from "react";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom";

/**
 * Houses component to display a list of houses with pagination.
 * @returns {JSX.Element} The Houses component JSX.
 */
const Houses = () => {
    const [houses, setHouses] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalHouses, setTotalHouses] = useState(0);

    useEffect(() => {
        const fetchHouses = async () => {
            try {
                const response = await IceAndFireApi.getHousesWithPagination(currentPage, pageSize);
                setHouses(response.data);
                setTotalHouses(response.total);
            } catch (error) {
                console.error("Error fetching houses:", error);
            }
        };

        fetchHouses();
    }, [currentPage, pageSize]);

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    const handlePageSizeChange = (newSize) => {
        setPageSize(newSize);
        setCurrentPage(1); // Reset to the first page when changing page size
    };

    const totalPages = Math.ceil(totalHouses / pageSize);

    return (
        <div>
            <h1>Houses of Westeros and Beyond</h1>
            <ul>
                { houses.map((house) => (
                    <li key={ house.url }>
                        <Link to={ `/houses/${house.url.split("/").pop()}` }>
                            { house.name }
                            <br />
                            Region: { house.region || "Unknown" }
                        </Link>
                    </li>
                )) }
            </ul>

            <div className="pagination">
                <button
                    onClick={ () => handlePageChange(currentPage - 1) }
                    disabled={ currentPage === 1 }
                >
                    &larr; Previous
                </button>
                <span>Page { currentPage } of { totalPages }</span>
                <button
                    onClick={ () => handlePageChange(currentPage + 1) }
                    disabled={ currentPage === totalPages }
                >
                    Next &rarr;
                </button>

                <select
                    value={ pageSize }
                    onChange={ (e) => handlePageSizeChange(parseInt(e.target.value, 10)) }
                >
                    <option value={ 10 }>10 per page</option>
                    <option value={ 20 }>20 per page</option>
                    <option value={ 50 }>50 per page</option>
                </select>
            </div>
        </div>
    );
};

export default Houses;
