import React, { useState, useEffect } from "react";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom"

/**
 * Characters component to display a list of characters.
 * @returns {JSX.Element} The Characters component JSX.
 */
const Characters = () => {
    //Initialize state variables for characters, current page, page size, and total characters
    const [characters, setCharacters] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalCharacters, setTotalCharacters] = useState(0);
    const [loading, setLoading] = useState(false);
    const [hasMore, setHasMore] = useState(true);
    const loadMoreCharacters = async () => {
        if (!loading) {
            setLoading(true);
            try {
                const nextPage = currentPage + 1;
                const response = await IceAndFireApi.getCharactersWithPagination(nextPage, pageSize);
                const newCharacters = response.data;
                setCharacters((prevCharacters) => [...prevCharacters, ...newCharacters]);
                setCurrentPage(nextPage);
                //Extract pagination links from the response headers
                const links = parseLinkHeader(response.headers.link);
                //Determine if there is a next page
                setHasMore(!!(links && links.next));
            } catch (error) {
                console.error("Error fetching characters:", error);
            }
            setLoading(false);
        }
    };
    useEffect(() => {
        loadMoreCharacters();
    }, [currentPage, pageSize]);
    return (
        <div>
            <h1>Characters of Westeros and Beyond</h1>
            <ul>
                { characters.map((character) => (
                    <li key={ character.url }>
                        <Link to={ `/characters/${character.url.split("/").pop()}` }>
                            { character.name || (character.aliases.length > 0 ? character.aliases[0] : "Unnamed") }
                            <br />
                            Culture: { character.culture || "Unknown" }
                            <br />
                            Gender: { character.gender === "male" ? "♂️" : character.gender === "female" ? "♀️" : "Unknown" }
                        </Link>
                    </li>
                )) }
            </ul>
            {}
        </div>
    );
};

export default Characters;
