import axios from "axios";

const BASE_URL = "https://anapioficeandfire.com/api"
/**
 * Class representing Ice and Fire API client
 */
class IceAndFireApi {
    /**
     * Fetches characters with pagination from the API.
     * @param {number} page - The page number to retrieve.
     * @param {number} pageSize - The number of items per page.
     * @returns {Promise} A promise that resolves to the characters data.
     */
    static async getCharactersWithPagination(page, pagesize) {
        const response = await axios.get(`${BASE_URL}/characters?page=${page}&pageSize=${pageSize}`);
        return response.data;
    }
    /**
     * Fetches a character by its ID from the API.
     * @param {string} id - The ID of the character.
     * @returns {Promise} A promise that resolves to the character data.
     */
    static async getCharacterById(id) {
        const response = await axios.get(`${BASE_URL}/characters/${id}`);
        return response.data;
    }
    /**
     * Fetches all the houses from the API.
     * @returns {Promise} A promise that resolves to the houses data.
     */
    static async getHouses() {
        const response = await axios.get(`${BASE_URL}/houses`);
        return response.data;
    }
    /**
     * Fetches an allegiance by its URL from the API.
     * @param {string} url - The URL of the allegiance.
     * @returns {Promise} A promise that resolves to the allegiance data.
     */
    static async getAllegianceByUrl(url) {
        const response = await axios.get(url);
        return response.data;
    }
}

export default IceAndFireApi;
