import React from "react"

/**
 * Pagination component for handling pagination UI.
 * @param {Object} props - The props for the Pagination component.
 * @param {number} props.currentPage - The current page number.
 * @param {number} props.pageSize - The number of items per page.
 * @param {number} props.totalItems - The total number of items.
 * @param {function} props.onPageChange - Function to handle page change.
 * @param {function} props.onPageSizeChange - Function to handle page size change.
 * @returns {JSX.Element} The Pagination component JSX.
 */
const Pagination = ({ currentPage, pageSize, totalItems, onPageChange, onPageSizeChange }) => {
    const totalPages = Math.ceil(totalItems / pageSize);
    /**
     * Handles the change of the current page and triggers the onPageChange function.
     * @param {number} newPage - The new page number.
     */
    const handlePageChange = (newPage) => {
        if (newPage >= 1 && newPage <= totalPages) {
            onPageChange(newPage);
        }
    };
    /**
     * Handles the change of the page size and triggers the onPageSizeChange function.
     * @param {Object} event - The change event from the page size select.
     */
    const handlePageSizeChange = (event) => {
        const newSize = parseInt(event.target.value, 10);
        onPageSizeChange(newSize);
    };
    return (
        <div className="pagination">
            <button onClick={ () => handlePageChange(currentPage - 1) } disabled={ currentPage === 1 }>
                { "<" }
            </button>
            <span>Page { currentPage } of { totalPages }</span>
            <button onClick={ () => handlePageChange(currentPage + 1) } disabled={ currentPage === totalPages }>
                { ">" }
            </button>
            <span>Page Size:</span>
            <select value={ pageSize } onChange={ handlePageSizeChange }>
                <option value={ 5 }>5</option>
                <option value={ 10 }>10</option>
                <option value={ 20 }>20</option>
            </select>
        </div>
    );
};

export default Pagination;
