import React, { useState, useEffect } from "react";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom";
import Pagination from "../../components/Pagination/Pagination";

const Houses = () => {
    const [houses, setHouses] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalHouses, setTotalHouses] = useState(0);
    const [error, setError] = useState(null);

    const totalPages = Math.ceil(totalHouses / pageSize);

    useEffect(() => {
        const fetchHouses = async () => {
            try {
                const response = await IceAndFireApi.getHousesWithPagination(
                    currentPage,
                    pageSize
                );
                if (response.data.length === 0) {
                    console.log("No more houses to list.");
                } else {
                    setHouses(response.data);
                    setTotalHouses(response.total);
                }
            } catch (err) {
                setError("Error fetching houses. Please try again later.");
            }
        };

        fetchHouses();
    }, [currentPage, pageSize]);

    const handlePageChange = (newPage) => {
        setCurrentPage(newPage);
    };

    const handlePageSizeChange = (newSize) => {
        setPageSize(newSize);
        setCurrentPage(1);
    };

    const fetchCharacterName = async (characterUrl) => {
        try {
            const characterResponse = await IceAndFireApi.getCharacterByUrl(
                characterUrl
            );
            return characterResponse.data.name;
        } catch (err) {
            console.error("Error fetching character:", err);
            return "N/A";
        }
    };

    const renderHouses = async () => {
        const renderedHouses = await Promise.all(
            houses.map(async (house) => {
                const currentLordName = await fetchCharacterName(house.currentLord);
                const swornMembersNames = await Promise.all(
                    house.swornMembers.map(async (memberUrl) =>
                        await fetchCharacterName(memberUrl)
                    )
                );

                return (
                    <li key={ house.url }>
                        <Link to={ `/houses/${house.url.split("/").pop()}` }>
                            <strong>{ house.name }</strong>
                            <br />
                            Titles: { house.titles.join(", ") || "No Titles" }
                            <br />
                            Current Lord: { currentLordName }
                            <br />
                            Sworn Members: { swornMembersNames.join(", ") || "N/A" }
                        </Link>
                    </li>
                );
            })
        );

        return renderedHouses;
    };

    return (
        <div>
            <h1>Houses of Westeros and Beyond</h1>
            { error && <p>{ error }</p> }
            <ul>{ renderHouses() }</ul>
            <Pagination
                currentPage={ currentPage }
                totalPages={ totalPages }
                onPageChange={ handlePageChange }
            />
            <select
                value={ pageSize }
                onChange={ (e) =>
                    handlePageSizeChange(parseInt(e.target.value, 10))
                }
            >
                <option value={ 10 }>10 per page</option>
                <option value={ 20 }>20 per page</option>
                <option value={ 50 }>50 per page</option>
            </select>
        </div>
    );
};

export default Houses;
