import React, { useEffect, useState } from "react";
import Pagination from "../../components/Pagination";
import IceAndFireApi from "../../services/IceAndFireApi";
import { Link } from "react-router-dom";

/**
 * Houses component to list all the houses.
 * @returns {JSX.Element} The Houses component JSX.
 */
const Houses = () => {
    const [houses, setHouses] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [pageSize, setPageSize] = useState(10);
    const [totalHouses, setTotalHouses] = useState(0);
    useEffect(() => {
        /**
         * Fetches houses from the API.
         */
        const fetchHouses = async () => {
            try {
                const response = await IceAndFireApi.getHouses();
                setHouses(response);
                setTotalHouses(response.length);
            } catch (error) {
                console.error("Error fetching houses:", error);
            }
        };
        //Fetch houses when the component mounts
        fetchHouses();
    }, []);
    return (
        <div>
            <h1>Houses of Westeros and Beyond</h1>
            <ul>
                { houses.map((house) => (
                    <li key={ house.url }>
                        <strong>{ house.name || "Unnamed House" }</strong>
                        <br />
                        { house.title.length > 0 ? `Titles: ${house.title.join(", ")}` : "No Titles" }
                        <br />
                        { house.currentLord && (
                            <Link to={ `/characters/${house.currentLord.split("/").pop()}` }>
                                Current Lord: Click To View
                            </Link>
                        ) }
                        <br />
                        { house.swornMembers.length > 0 && (
                            <div>
                                Sworn Members:
                                <ul>
                                    { house.swornMembers.map((swornMember) => (
                                        <li key={ swornMember.split("/").pop() }>
                                            <Link to={ `/characters/${swornMember.split("/").pop()}` }>
                                                { swornMember.split("/").pop() }
                                            </Link>
                                        </li>
                                    )) }
                                </ul>
                            </div>
                        ) }
                    </li>
                )) }
            </ul>
            <Pagination
                currentPage={ currentPage }
                pageSize={ pageSize }
                totalItems={ totalHouses }
                onPageChange={ (page) => setCurrentPage(page) }
                onPageSizeChange={ (size) => setPageSize(size) }
            />
        </div>
    );
};

export default Houses;
